mapper.insert:
/*AppointmentMapper.insertAppointment*/
INSERT INTO tb_appointment (
         appointment_id
        ,customer_uid
        ,designer_uid
        ,designer_shop_id
        ,appointment_status_code
        ,appointment_start_type_code
        ,total_amount
        ,appointment_amount
        ,treatment_start_at
        ,treatment_end_at
        ,payment_method_code
        ,appointment_content
        ,cancel_reason_content
        ,review_id
        ,customer_name
        ,customer_nickname
        ,customer_contact
        ,designer_name
        ,designer_nickname
        ,designer_contact
        ,shop_name
        ,shop_addr
        ,create_at
        ,create_id
) VALUES (
          nextVal('seq_tb_appointment')
         ,#{customerUid}
         ,#{designerUid}
         ,#{designerShopId}
         ,#{appointmentStatusCode}
         ,#{appointmentStartTypeCode}
         ,#{totalAmount}
         ,#{appointmentAmount}
         ,#{treatmentStartAt}
         ,#{treatmentEndAt}
         ,#{paymentMethodCode}
         ,#{appointmentContent}
         ,#{cancelReasonContent}
         ,#{reviewId}
         ,#{customerName}
         ,#{customerNickname}
         ,#{customerContact}
         ,#{designerName}
         ,#{designerNickname}
         ,#{designerContact}
         ,#{shopName}
         ,#{shopAddr}
         ,NOW()
         ,#{createId}
                 )

mapper.update:
/*AppointmentMapper.deleteAppointment*/
UPDATE  tb_appointmenttb_appointment

    delete_yn = 'Y'
    delete_at =  NOW()
    delete_id = #{designerUid}

WHERE appointment_id = #{appointmentId}
